import React from "react";
import '../../styles/styles.css';

export default function EditorSection(){
    return(
        <div class="editor-section">
            <h1 class="title">Bài 31</h1>
            <p class="content">Viết chương trình lấy số nguyên n từ người dùng và hiển thị\n 
                tổng các số từ 1 đến n trên màn hình. Ví dụ, nếu n = 5,\n 
                chương trình sẽ cho kết quả như sau:\n 
                1 + 2 + 3 + 4 + 5 = 15</p>
        </div>
    );
};
