import React, { Component } from 'react';
import axios from 'axios';
import Questions from './components/Questions';
import SplitPane from 'react-split-pane';
import EditorSection from './components/layout/editor-section';
import ProblemSection from './components/layout/problem-section';
import TestcaseSection from './components/layout/testcase-section';
import { NavBar } from './components/nav/navbar';

import './App.css';
import './styles/styles.css';


export default class App extends Component {
  state = {
    questions: [],
    loading: false,
    currentQuestion: 1
  }

  componentDidMount() {
    const getQuestions = async () => {
      this.setState({loading: true});
      const results = await axios.get(`http://localhost:3001`);
      this.setState({questions: results.data});
      this.setState({loading: false});
      console.log(results.data);
    };
  }

  render() {
    const { questions, currentQuestion, loading } = this.state;

    return (
      <div className='container'>
        <Questions questions={currentQuestion} loading={loading}>

        </Questions>
      </div>
    )
  }
}
