import React, { useState, useEffect } from 'react';

import SplitPane from 'react-split-pane';
import Editor from './components/layout/Editor';
import Problem from './components/layout/Problem';
import Testcase from './components/layout/Testcase';
import PaginationBasic from './components/nav/Pagination';

import './App.css';
import './styles/styles.css';

function App() {
  const [questions, setQuestion] = useState([]);
  // const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(questions[1]);

  // useEffect(() => {
  //   const fetchQuestions = async () => {
  //     setLoading(true);
  //     const res = await axios.get('http://localhost:3001');
  //     setQuestion(res.data);
  //     setLoading(false);
  //   }

  //   fetchQuestions();
  // },[])

  useEffect(() => {
    fetch("http://localhost:3001")
      .then(res => res.json())
      .then(data => {
          setQuestion(data.data);
          // setLoading(true);
        },
        (error) => {
          setQuestion(error);
        }
      )
  }, []);

  // Change page
  const paginate = pageNumber => setCurrentPage(pageNumber);

  return (
    <div className="App">
      <PaginationBasic question={questions} paginate={paginate}/>
      <SplitPane
      split='vertical'
      defaultSize='45%'
      minSize={400}
      maxSize={900}
      >
        <Problem question={currentPage}/>
        <div class="right">
          <Editor/>
          <Testcase question={currentPage}/>
        </div>
      </SplitPane>
    </div>
  );
};

export default App;
