import React, { useState, useEffect } from 'react';

import SplitPane from 'react-split-pane';
import Editor from './components/layout/Editor';
import Problem from './components/layout/Problem';
import Testcase from './components/layout/Testcase';
import PaginationBasic from './components/nav/Pagination';

import './App.css';
import './styles/styles.css';

function App() {
  const [questions, setQuestion] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);

  const changePage = (page) => {
    // if (page > questions.length - 1) {
    //   setCurrentPage(questions.length);
    // } else if (page < 1) {
    //   setCurrentPage(1);
    // }
    setCurrentPage(page);
  }

  useEffect(() => {
    fetch("http://localhost:3001")
      .then(res => res.json())
      .then(data => {
          setQuestion(data.data);
        },
        (error) => {
          setQuestion(error);
        }
      )
  }, []);

  // Get current question
  const currentQuestion = questions.slice(currentPage - 1, currentPage);

  return (
    <div className="App">
      <PaginationBasic 
        totalQuestion={questions.length} 
        paginate={changePage(currentPage)}
        nextPage={changePage(currentPage + 1)}
        prevPage={changePage(currentPage - 1)}
        />
      <SplitPane
      split='vertical'
      defaultSize='45%'
      minSize={400}
      maxSize={900}
      >
        <Problem question={currentQuestion}/>
        <div class="right">
          <Editor/>
          <Testcase question={currentQuestion}/>
        </div>
      </SplitPane>
    </div>
  );
};

export default App;
